import { Component } from '@angular/core';

@Component({
  selector: 'app-hello-world',
  templateUrl: './hello-world.component.html',
  styleUrls: ['./hello-world.component.scss'],
})
export class HelloWorldComponent {
  message: any = "Checking both Event and Property Binding";
  message11: String = "2 way binding works!!";
  disableBtn: boolean = false;
  updateMessage() {
    this.message = 'Message updated on button click!';
    this.disableBtn = true;
  }
}
<div class="d-flex flex-row justify-content-center">
  <h1>Welcome to My Angular App</h1>
  <p>{{ message }}</p>
  <button (click)="updateMessage()">Update Message</button>
  <button [disabled]="disableBtn">Will be disabling</button>
<input type="text" [(ngModel)]="message11" value="">
<p>{{message11}}</p>
</div>
